import React from 'react'
import './LoginForm.css'

class LoginForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            login: '',
            mdp: ''
        }
        this.handleLoginChange = this.handleLoginChange.bind(this);
        this.handleMdpChange = this.handleMdpChange.bind(this);
        this.handleConnection = this.handleConnection.bind(this);
        this.handleSubscribe = this.handleSubscribe.bind(this);
    }
    handleLoginChange(e) {
        this.setState({ login: e.target.value })
    }
    handleMdpChange(e) {
        this.setState({ mdp: e.target.value })
    }
    handleConnection(e) {
        const pseudo = this.state.login
        const mdp = this.state.mdp
        fetch(`http://localhost:3000/users/authenticate`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ pseudo: `${pseudo}`, mdp: `${mdp}` })
        })
            .then((res) => res.ok ? res.text() : "L'utilisateur n'existe pas")
            .then((res) => alert(res));
        // Le res de alert est le résultat du premier then
    }
    handleSubscribe(e) {

    }
    render() {
        return (
            <div className="LoginForm">
                <input type='text' value={this.state.login} placeholder='Pseudonyme' onChange={this.handleLoginChange} ></input>
                <input type='password' value={this.state.mdp} placeholder='Mot de passe' onChange={this.handleMdpChange}></input>
                <button onClick={this.handleConnection}>{"Se connecter"}</button>
                <button onClick={this.handleSubscribe}>{"S'inscrire"}</button>
            </div>)
    }
}
export default LoginForm
