import React from 'react'
import './LoginForm.css'

class LoginForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            login: '',
            mdp: ''
        }
    }
    handleLoginChange() {

    }
    handleMdpChange(){
        this.setState({})
    }
    handleConnection() {
        console.log(this.state.login)
    }
    handleSubscribe() {
        this.setState()
    }
    render() {
        return (
            <div className="LoginForm">
                <input type='text' value={this.state.login} placeholder='Pseudonyme' onChange={() => this.handleLoginChange()} ></input>
                <input type='text' value={this.state.mdp} placeholder='Mot de passe' onChange={() => this.handleMdpChange()}></input>
                <button onClick={() => this.handleConnection()}>{"Se connecter"}</button>
                <button onClick={() => this.handleSubscribe()}>{"S'inscrire"}</button>
            </div>)
    }
}

